// Generated by CoffeeScript 1.6.3
Datafly.alert = function(el) {
  return $('#alert' + el).slideDown('slow').delay(6000).slideUp('slow');
};

Datafly.submit = function(event) {
  var $form, redirect, reload, success;
  event.preventDefault();
  $form = $(this).parents('form');
  reload = $(this).data('reload');
  redirect = $(this).data('redirect');
  success = $(this).data('success');
  return $.ajax({
    url: $form.attr('action'),
    type: 'POST',
    data: $form.serialize()
  }).done(function(data) {
    success = success || data.success;
    if (reload) {
      return window.location.reload();
    } else if (success) {
      return Datafly.alert(success);
    } else if (data.error === false) {
      return location.href = redirect || data.redirect;
    } else {
      return Datafly.alert(data.error);
    }
  });
};
// Generated by CoffeeScript 1.6.3
Datafly.redactor_buttons = ['html', 'formatting', '|', 'bold', 'italic', 'link', '|', 'unorderedlist', 'orderedlist', '|', 'alignment', '|', 'indent', 'outdent', '|', 'image', 'file', 'video'];

$(function() {
  $('.redactor').redactor({
    buttons: Datafly.redactor_buttons,
    imageUpload: '/admin/upload/img',
    fileUpload: '/admin/upload/file',
    toolbarExternal: '#redactor-toolbar'
  });
  $('#save').click(function() {
    var data, id, url;
    data = {};
    $('.redactor').each(function() {
      var clip, html;
      clip = $(this).data('clip');
      html = $(this).redactor('get');
      return data[clip] = html;
    });
    id = $(this).data('page-id');
    url = "/api/pages/" + id;
    return $.ajax({
      url: url,
      type: 'POST',
      data: data
    });
  });
  return $('#cancel').click(function() {
    return location.reload();
  });
});
